export default function EmojiPickerBackground(picker, setPicker, handleEmoji) {
  return (
    <div className="relative flex justify-between p-[10px_15px] items-end post_emojis_wrap">
    {picker&&(
        <div className="absolute right-[-21.5rem] bottom-[-14rem] comment_emoji_picker z-50"> 
        <Picker onEmojiClick={handleEmoji}/>
    </div>
    )}

    <img
        src="../../../icons/colorful.png"
        className="w-[39px] cursor-pointer"
        alt=""/>
        <i
      className="hover:invert-[80%] emoji_icon_large"
      onClick={() => setPicker((prev) => !prev)}
    ></i>
</div>
  )
}
