import Header from "../../components/header";
import LeftHome from "../../components/home/left";
import { useSelector } from "react-redux";
import RightHome from "../../components/home/right";
import Stories from "../../components/home/stories";
import "./style.css";
import CreatePost from "../../components/createPosts";
import ActivateForm from "./ActivateForm";
import { useState } from "react";

export default function Activate(){
  const {user} = useSelector((user) => ({...user}));
  const [loading, setLoading] = useState(true);
  const [success, setSuccess] = useState("Your account has been activated successfully. You can now login to your account.");
  const [error, setError] = useState("This email is already activated.");


  return (
    <div className="home">
       {success && (
          <ActivateForm
            type="success"
            header="Account Activation Succeeded"
            text={success}
            loading={loading}
          />
        )}
        {error && (
          <ActivateForm
            type="error"
            header="Account Activation failed"
            text={error}
            loading={loading}
          />
        )}
      <Header />
      <LeftHome user={user}/>
      <div className="absolute mt-[75px] left-[50%] translate-x-[-50%] home_middle">
        <Stories />
        <CreatePost user={user}/>
      </div>
      <RightHome />
    </div>


  );
}
