import { Link } from 'react-router-dom';
import './style.css';
import Moment from 'react-moment';
import { Dots, Public } from '../../svg';
import ReactsPopup from './ReactsPopup';
import { useState } from 'react';
import CreateComment from './CreateComment';
import PostMenu from './PostMenu';

export default function Post({ post, user, profile }) {
  const [visible, setVisible] = useState(false);
  const [showMenu, setShowMenu] = useState(false);

  return (
    <div className="post" style={{width: `${profile && "100%"}`}}>
      <div className="post_header">
        <Link to={`/profile/${post.user.username}`} className="post_header_left">
          <img src={post?.user?.picture} alt="" />
          <div className="header_col">
            <div className="post_profile_name">
              {post?.user?.first_name} {post?.user?.last_name}
              <div className="updated_p">
                {post.type === "profilePicture" && `updated ${post?.user?.gender === "male" ? "his" : "her"} profile picture`}
                {post.type === "cover" && `updated ${post?.user?.gender === "male" ? "his" : "her"} cover picture`}
              </div>
            </div>
            <div className="post_profile_privacy_date">
              <Moment fromNow interval={30}>{post.createdAt}</Moment>
              . <Public color="#828387" />
            </div>
          </div>
        </Link>
        <div className="post_header_right hover:hover1" onClick={() => {setShowMenu((prev) => !prev)}}>
          <Dots color="#828387" />
        </div>
      </div>
      {post.background ? (
        <div className='post_bg' style={{backgroundImage : `url(${post.background})`}}>
          <div className="post_bg_text">{post.text}</div>
        </div>
      ) : (
        <>
          <div className="post_text">{post.text}</div>
          {post.images && post.images.length && (
            <div className={
              post.images.length === 1
              ? "grid-1"
              : post.images.length === 2
              ? "grid-2"
              : post.images.length === 3
              ? "grid-3"
              : post.images.length === 4
              ? "grid-4"
              : post.images.length >= 5
              && "grid-5"
            }>
              {post.images.slice(0, 5).map((image, i) => (
                <img src={image.url} key={i} alt="" className={`img-${i}`} />
              ))}
              {
                post.images.length > 5 && (
                  <div className="more_pics_shadow">+{post.images.length - 5}</div>
                )
              }
            </div>
          )}
        </>
      )}
      <div className="post_infos">
        <div className="reacts_count">
          <div className="reacts_count_imgs"></div>
          <div className="reacts_count_num"></div>
        </div>
        <div className="to_right">
          <div className="comments_count">13 comments</div>
          <div className="share_count">1 share</div>
        </div>
      </div>
      <div className="post_actions">
        <ReactsPopup visible={visible} setVisible={setVisible} />
        <div className="post_action hover:hover1" onMouseOver={() => {setTimeout(() => {setVisible(true)},500)}} onMouseLeave={() => {setTimeout(() => {setVisible(false)},500)}}>
          <i className="like_icon"></i>
          <span>Like</span>
        </div>
        <div className="post_action hover:hover1">
          <i className="comment_icon"></i>
          <span>Comment</span>
        </div>
        <div className="post_action hover:hover1">
          <i className="share_icon"></i>
          <span>Share</span>
        </div>
      </div>
      <div className="comments_wrap">
        <div className="comments_order">
          <CreateComment user={user} />
        </div>
      </div>
      {
        showMenu && <PostMenu userId={user.id} postUserId={post?.user?._id} imagesLength={post?.images?.length} setShowMenu={setShowMenu} />
      }
    </div>
  )
}
