import Header from "../../components/header";
import LeftHome from "../../components/home/left";
import { useSelector } from "react-redux";
import RightHome from "../../components/home/right";
import Stories from "../../components/home/stories";
import "./style.css";
import CreatePost from "../../components/createPosts";
import ActivateForm from "./ActivateForm";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import axios from "axios";
import { navigate } from "@reach/router";
import Cookies from "js-cookie";
import { useDispatch } from "react-redux";
const dispatch = useDispatch();
import { setActivate } from "../../redux/actions/userActions";


export default function Activate(){
  const {user} = useSelector((user) => ({...user}));
  const [loading, setLoading] = useState(true);
  const [success, setSuccess] = useState("Your account has been activated successfully. You can now login to your account.");
  const [error, setError] = useState("This email is already activated.");
  const {token} = useParams();
    // console.log(token);
    useEffect(() => {
        activateAccount();
      }, []);
      const activateAccount = async () => {
        try {
          setLoading(true);
          const { data } = await axios.post(
            `${process.env.REACT_APP_BACKEND_URL}/activate`,
            { token },
            {
              headers: {
                Authorization: `Bearer ${user.token}`,
              },
            }
          );
          setActivate(true);
          setSuccess(data.message);
          Cookies.set("user", JSON.stringify({ ...user, verified: true }));
          dispatch({
            type: "VERIFY",
            payload: true,
          });
          setTimeout(() => {
            navigate("/");
          }, 3000);
          setLoading(false);
        } catch (error) {
          setError(error.response.data.message);

          setTimeout(() => {
            navigate("/");
          }, 3000);
        }
      };


  return (
    <div className="home">
       {success && (
          <ActivateForm
            type="success"
            header="Account Activation Succeeded"
            text={success}
            loading={loading}
          />
        )}
        {error && (
          <ActivateForm
            type="error"
            header="Account Activation failed"
            text={error}
            loading={loading}
          />
        )}
      <Header />
      <LeftHome user={user}/>
      <div className="absolute mt-[75px] left-[50%] translate-x-[-50%] home_middle">
        <Stories />
        <CreatePost user={user}/>
      </div>
      <RightHome />
    </div>


  );
}
