import {useField} from 'formik';

export default function LoginInput({placeholder, ...props}) {
    const[field,meta] = useField(props)
  return (
  <div className="realtive w-[320px] flex flex-col items-center">

    <div>
        {meta.touched && meta.error }
    </div>
    <input 
    type={field.type}
    name={field.name}
    placeholder ={placeholder}
    {...field}
    {...props} 
    className="outline-none border-[1px] border-solid border-third bg-primary w-[100%] h-[50px] text-[17px] rounded-[10px] pl-[10px] mb-[10px] text-primary-color focus:border-blue-color"/>
    </div>
    );

}
